import React, { useState } from "react";
import ReactDOM from 'react-dom';
import Funcionario from './Funcionario'
import Api from './Api'

const ListaFuncionarios = () => {

    let [listFuncionario, setListFuncionario] = useState([]);
    let [ready, setReady] = useState(false);

    const consultaListaFuncionarios = function() {
        Api.call(Api.baseUrl+"/funcionario/list", "GET", null, (data) => {
            if (data) {
                setListFuncionario(data);
                setReady(true);
            }
        });
    }

    const deletaFuncionario = function(funcionario, callBackFunction) {
        if (confirm("Deseja Realmente deletar este registro?")) {
            Api.call(Api.baseUrl+"/funcionario/delete", "DELETE", funcionario, () => {
                consultaListaFuncionarios();
            });
        }
    }

    // Api.consultaListaFuncionarios((data) => {
    //     if (data) {
    //         setListFuncionario(data);
    //         setReady(true);
    //     }
    // })

    const novoFuncionario = function (evt) {
        // return <Funcionario id={evt.target.id} />;
        ReactDOM.render(<Funcionario id={null} />, document.getElementById('root'));
    }

    const editaFuncionario = function(evt) {
        // return <Funcionario id={evt.target.id} />;
        ReactDOM.render(<Funcionario id={evt.target.id} />, document.getElementById('root'));
        // Api.open('Funcionario');
    }

    // const deletaFuncionario = function(evt) {
    //     Api.deletaFuncionario(evt.target.id);
    // }

    consultaListaFuncionarios();

    const getLinhasFuncionarios = function() {

        return listFuncionario.map( funcionario => {
            return <tr id={funcionario.id}>
                <td><input id={funcionario.id} type="button" value="Editar" onClick={editaFuncionario} /></td>
                <td>{funcionario.id}</td>
                <td>{funcionario.nome}</td>
                <td>{funcionario.sobrenome}</td>
                <td><input id={funcionario.id} type="button" value="Excluir" onClick={deletaFuncionario} /></td>
            </tr>
        })
    }

    return (
        <>
            {ready && 
                <div>
                    <h1>Listagem Funcion√°rios</h1>
                    <input type="button" value="Novo" onClick={novoFuncionario} />
                    <table heigth='40%' width="50%">
                        <thead>
                            <td width="5%">#</td><td width="10%">ID</td><td width="40%">Nome</td><td width="40%">Sobrenome</td><td width="5%">#</td>
                        </thead>
                        <tbody overflow="auto">
                            {getLinhasFuncionarios()}
                        </tbody>
                    </table>
                </div>
            }
            </>
    )
}
export default ListaFuncionarios;
