import React, { useState, useEffect } from "react";
import ReactDOM from 'react-dom';
import Api from './Api'
import ListaFuncionarios from './ListaFuncionarios'

const Funcionario = (props) => {

    // let [funcionario, setFuncionario] = useState({nome: '', sobrenome: '', email:'' , numeroNIS: ''});

    let [id, setId] = useState(null);
    let [nome, setNome] = useState('');
    let [sobrenome, setSobrenome] = useState('');
    let [email, setEmail] = useState('');
    let [numeroNIS, setNumeroNIS] = useState(0);

    const getFuncionario = function() {
        return {
            id: id,
            nome: nome,
            sobrenome: sobrenome,
            email: email,
            numeroNIS: numeroNIS
        };
    }

    const setFuncionario = function(obj) {
        setId(obj.id);
        setNome(obj.nome);
        setSobrenome(obj.sobrenome);
        setEmail(obj.email);
        setNumeroNIS(obj.numeroNIS);
    }

    const salvaFuncionario = function() {
        let funcionario = getFuncionario();
        let method = funcionario.id ? "PUT" : "POST";
        Api.call(Api.baseUrl+"/funcionario/"+(method === "POST" ? "insert" : "update"), method, funcionario, () => {
            ReactDOM.render(<ListaFuncionarios />, document.getElementById('root'));
        });
    }

    const fechaCadastro = function() {
        ReactDOM.render(<ListaFuncionarios />, document.getElementById('root'));
    }

    useEffect(() => {
        if (props.id) {
            Api.call(Api.baseUrl+"/funcionario/"+props.id, "GET", null,  (data) => {
                if (data) {
                    setFuncionario(data);
                }
            })
        }
    }, [])

    return (
        <div>
            <div className="form">
                <div>
                    <h1>Edição de Funcionário ({props.id})</h1>
                </div>
                <form>
                    <label>Nome</label>
                    <input type='text' name='nome' value={nome} minLength='2' maxLength='30' onChange={(e) => {setNome(e.target.value)}}/>

                    <label>Sobrenome</label>
                    <input type='text' name='sobrenome' minLength='2' maxLength='50' value={sobrenome}  onChange={(e) => {setSobrenome(e.target.value)}} />

                    <label>E-mail</label>
                    <input type='email' name='email' value={email} onChange={(e) => {setEmail(e.target.value)}} />

                    <label>Número NIS</label>
                    <input type='number' name='numeroNIS' value={numeroNIS} onChange={(e) => {setNumeroNIS(e.target.value)}} />
                    <br/><br/>
                    <input type='button' value='Confirma' onClick={salvaFuncionario} />
                    <br/>
                    <input type='button' value='Fechar' onClick={fechaCadastro} />
                </form>
            </div>
        </div>
    )
}
export default Funcionario;
