import React, { useState, useEffect } from "react";
import ReactDOM from 'react-dom';
import Funcionario from './Funcionario'
import Api from './Api'

const ListaFuncionarios = () => {

    let [listFuncionario, setListFuncionario] = useState([]);
    let [refresh, setRefresh] = useState(false);

    useEffect(() => {
        Api.call(Api.baseUrl+"/funcionario/list", "GET", null, (data) => {
            if (data) {
                setListFuncionario(data);
            }
        });
    }, [])

    const deletaFuncionario = function(id) {
        if (id) {
            Api.call(Api.baseUrl+"/funcionario/delete/"+id, "DELETE", null, (res) => {
                // alert("Funcion√°rio deletado com sucesso!");
                setRefresh(true);
                ReactDOM.render(<ListaFuncionarios />, document.getElementById('root'));
            });
        }
    }

    const novoFuncionario = function (evt) {
        ReactDOM.render(<Funcionario id={null} />, document.getElementById('root'));
    }

    const editaFuncionario = function(id) {
        // return <Funcionario id={evt.target.id} />;
        ReactDOM.render(<Funcionario id={id} />, document.getElementById('root'));
        // Api.open('Funcionario');
    }

    const getLinhasFuncionarios = function() {

        return listFuncionario.map( funcionario => {
            return <tr id={funcionario.id}>
                <td><input id={funcionario.id} type="button" value="Editar" onClick={(e) => {editaFuncionario(e.target.id)}} /></td>
                <td>{funcionario.id}</td>
                <td>{funcionario.nome}</td>
                <td>{funcionario.sobrenome}</td>
                <td><input id={funcionario.id} type="button" value="Excluir" onClick={(e) => {deletaFuncionario(e.target.id)}} /></td>
            </tr>
        })
    }

    return (
        <>
            {/* {ready &&  */}
                <div>
                    <h1>Listagem Funcion√°rios</h1>
                    <input type="button" value="Novo" onClick={novoFuncionario} />
                    <table heigth='40%' width="50%">
                        <thead>
                            <td width="5%">#</td><td width="10%">ID</td><td width="40%">Nome</td><td width="40%">Sobrenome</td><td width="5%">#</td>
                        </thead>
                        <tbody overflow="auto">
                            {getLinhasFuncionarios()}
                        </tbody>
                    </table>
                </div>
            {/* } */}
            </>
    )
}
export default ListaFuncionarios;
